mapper.update:
UPDATE BOARD
SET
MODIFIED_DATE = NOW(),
TITLE = #{title},
CONTENT = #{content},
BOARD_IMG = #{boardImg}
WHERE BOARD_NO = #{boardNo}
AND USER_ID = #{userId}

UPDATE BOARD
SET
MODIFIED_DATE = NOW(),
DELETE_AT = TRUE
WHERE BOARD_NO = #{boardNo}
AND USER_ID = #{userId}

mapper.select:
SELECT * FROM BOARD WHERE DELETE_AT = FALSE AND BOARD_TYPE_NO = 33
AND searchWord LIKE CONCAT('%', #{title}, '%')
AND searchWord LIKE CONCAT('%', #{content}, '%')
AND CATEGORY_NO = #{categoryNo}
AND USER_ID = #{userId}

SELECT CNT, (SELECT BOARD_TYPE_NO FROM BOARD ) BOARD_TYPE_NO FROM SELECT COUNT(*) CNT FROM BOARD WHERE BOARD_TYPE_NO = 33 AND DELETE_AT = FALSE
AND searchWord LIKE CONCAT('%', #{title}, '%')
AND searchWord LIKE CONCAT('%', #{content}, '%')
AND CATEGORY_NO = #{categoryNo}
AND USER_ID = #{userId}

SELECT BOARD_NO, USER_ID
FROM LIKE
WHERE BOARD_NO = #{boardNo}
AND USER_ID = #{userId}

mapper.update:
UPDATE BOARD
SET
LIKE_COUNT = #{likeCount}
WHERE BOARD_NO = #{boardNo}
AND USER_ID = #{userId}

UPDATE BOARD
SET
BOARD_ZZIM = #{boardZzim}
WHERE BOARD_NO = #{boardNo}
AND USER_ID = #{userId}
